import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import CoinFullData from '../coinfulldata/CoinFullData';
import CoinChart from '../coinchart/CoinChart'
import axios from 'axios';

const CoinDetails = () => {
  const {id} = useParams();
  const [coinData, setCoinData] = useState([]);

  useEffect(() => {
      const fetchData = async () => {
          const dayRes = await axios.get(`https://api.coingecko.com/api/v3/coins/${id}/market_chart/`, {
            params: {
                vs_currency: "usd",
                days: "1",
            },
          });
          const weekRes = await axios.get(`https://api.coingecko.com/api/v3/coins/${id}/market_chart/`, {
            params: {
                vs_currency: "usd",
                days: "7",
            },
          });
          const yearRes = await axios.get(`https://api.coingecko.com/api/v3/coins/${id}/market_chart/`, {
            params: {
                vs_currency: "usd",
                days: "8",
            },
          });
          setCoinData({day: dayRes.price, day: weekRes.price, day: yearRes.price});
      };
      fetchData();
  },[]);

    const renderData = () => {
       return (
           <div className="coinlist">
                <CoinFullData />
                <CoinChart />
           </div>
       ) 
    }

  return renderData()
};


export default CoinDetails;
