import React from "react";
import { Link } from "react-router-dom";
import './usercoin.css';

const UserCoin = ({ coin, deleteCoin }) => {
    return (
      <Link to={`/usercoins/${coin.id}`} className="text-decoration-none my-1 coin">
        <li className="coinlist-item list-group-item list-group-item-action d-flex justify-content-between align-items-center text-dark">
          <img className="coinlist-image" src={coin.image} alt="" />
          <span className="text-decoration-none coin-price">${coin.current_price}</span>

          <span
            className={
              coin.price_change_percentage_24h < 0
                ? "text-danger mr-2"
                : "text-success mr-2"
            }
          >
            {" "}
            {coin.price_change_percentage_24h < 0 ? (
              <i className="fas fa-sort-down align-middle mr-1"></i>
            ) : (
              <i className="fas fa-sort-up align-middle mr-1"></i>
            )}
            {coin.price_change_percentage_24h}%
          </span>
          <button
            onClick={(e) => {
              e.preventDefault();
              deleteCoin(coin.id);
            }}
            className="delete-icon far fa-times-circle text-danger">X</button>
        </li>
      </Link>
    );
  };

  export default UserCoin;
